import { Component, OnInit } from '@angular/core';
import { Products } from '../products.model';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-product-data',
  templateUrl: './product-data.component.html',
  styleUrls: ['./product-data.component.css']
})


export class ProductDataComponent implements OnInit {
  id : number;
filterSearch='' ;

  constructor(private router: Router,private route:ActivatedRoute, private toastr: ToastrService) { 
    this.id=this.route.snapshot.params['id'];
  }


  ngOnInit(): void {
  }

  

  availProducts : Products[] = [
    new Products(1,'iPhone SE', 'Apple' , 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img1.webp', 1, 1000),
    new Products(2,'S22', 'Samsung' , 'https://m.media-amazon.com/images/I/71HUnJvHsbL._SL1500_.jpg', 1, 500),
    new Products(3,'OnePlus Nord', 'oneplus' , 'data:image/jpeg;base64,', 1, 400),
    new Products(4,'Mi 10T', 'Mi' , 'data:image/jpeg;base64,', 1, 300),
    new Products(5,'iPhone 14', 'Apple' , 'data:image/jpeg;base64,', 1, 500),
    new Products(6,'onePlus 10R', 'oneplus' , 'data:image/jpeg;base64,', 1, 500),
  ]

  gotoBascket(){
    this.router.navigate(['products/nextpage', this.id]);
  }

  inc(product:any){
    // console.log(product);
    if(product.quantity !=5){
      product.quantity += 1;
    }
  }

  dec(product: any){
    if(product.quantity !=1){
      product.quantity -= 1;
    }
  }

  itemsCart:any = [];
  addCart(category:any){
    let cartDataNull = localStorage.getItem('localCart');
    if(cartDataNull == null){
      let storeDataGet:any = [];
    storeDataGet.push(category);
      localStorage.setItem('localCart', JSON.stringify(storeDataGet));
    }else{
      var id = category.prodId;
      let index:number = -1;
      this.itemsCart = JSON.parse(localStorage.getItem('localCart') || "[]");
  for(let i=0; i<this.itemsCart.length; i++){
  if(parseInt(id) === parseInt(this.itemsCart[i].prodId)){
        this.itemsCart[i].quantity = category.quantity;
          index = i;
          break;
        }
      }
      if(index == -1){
    this.itemsCart.push(category);
    localStorage.setItem('localCart', JSON.stringify(this.itemsCart));
      }else{
        localStorage.setItem('localCart', JSON.stringify(this.itemsCart));
      }
    }

    // this.toastr.success(category.quantity + "items added");
    this.toastr.success('',category.quantity + "items added", {
      positionClass: 'toast-top-right' 
   });
    }
    
   
  }



